mapper.select:
	SELECT U_ID, U_PASS
 	FROM B_USER
 	WHERE U_ID=#{u_id} 
 	AND U_PASS=#{u_pass}

mapper.update:
UPDATE B_USER 
SET NICK = #{nick}, ADDRESS = #{address}, POST = #{post} ,QUESTION =#{question}, ANSWER = #{answer}
WHERE U_ID = #{u_id}

UPDATE B_USER
SET U_PASS = #{u_pass}
WHERE U_ID = #{u_id}

UPDATE B_USER
SET EMAIL = #{email}
WHERE U_ID = #{u_id}

UPDATE B_USER
SET PHONE = #{phone}
WHERE U_ID = #{u_id}

mapper.select:
SELECT COUNT(*)
FROM b_user
WHERE U_ID=#{u_id}

SELECT * 
FROM b_user
WHERE U_ID=#{u_id} AND U_PASS=#{u_pass}
